mapper.insert:
INSERT 
  INTO 
	   TB_MILEAGE
	   (
	   USER_ID
	 , MILEAGE_TITLE
	 , MILEAGE_CONTENT
	 , MILEAGE_CATEGORY
	 , MILEAGE_IMG
	   )
VALUES 
       (
	   #{userId}
	 , #{mileageTitle}
	 , #{mileageContent}
	 , #{mileageCategory}
	 , #{mileageImg}
	   )

mapper.update:
UPDATE
	TB_MILEAGE
SET 
	MILEAGE_STATUS = #{mileageStatus} 
WHERE 
	MILEAGE_SEQ = #{mileageSeq}
